sys_script_include.script:
var GetUserInfo_001 = Class.create();
GetUserInfo_001.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    GetUserInfo_001: function() {
        //var user_id = this.getParameter('sysparm_user_id');
        var user_name = this.getParameter('sysparm_user_name');
        var grUser = new GlideRecord('sys_user'); //SystemNowのユーザテーブル（sys_id）を参照する
        if (grUser.get(user_name)) { //指定したユーザが存在するか確認
            return grUser.name + '  [' + grUser.user_name + ']'; //存在する場合はメールアドレスを返す
        }
        return ''; //存在しない場合は空文字を返す
    },

    type: 'GetUserInfo_001'
});
