// jQuery部分で動かず！！！
function getName() {
     //resolve incidentボタンの色を変える
	changeButtonColor('#f0f8ff');
     //aaaaaaaaaボタンの色を変える
     //changeButtonColor('#ffffe0');
}
//第一引数:buttonID=>UI ActionでのAction nameの値,を指定
//第二引数:カラーコードを指定
//カラーコード:https://www.colordic.org/
function changeButtonColor(backgroundColor) {
     try{
           //buttonIDからbuttonを特定して 背景色を変更
           //$$('button[id=f4162ac22f3724106398f3ecf699b636]').each(function(elmt) {
           $$('button[id=57dc4c970a0a0b3400f8f0538d3faf94]').each(function(elmt) {
                 g_form.addInfoMessage('色変えるよ！');
                 elmt.style.backgroundColor = backgroundColor;
                 elmt.style.color = '#ffffff'; //ボタンのテキスト色を変更
           });
     }catch(e){}
}

