function testAjax() {

	var ajax = new GlideAjax('getApprovalQualifier'); 
	ajax.addParam('sysparm_name', 'getQualifier'); 
	//ajax.addParam('sysparm_p1', 'p1パラメータ'); 
	//ajax.addParam('sysparm_user_id', newValue); 
	ajax.getXMLAnswer(function(answer) {
		g_form.addInfoMessage('コール成功：');

// 		var st ='';
// 		for (var x in answer) {
// 			st += x + '//';
// 		}
// 		g_form.addInfoMessage(st);

		if (answer.length > 0) { 
			g_form.addInfoMessage(answer);
			//g_form.setValue('debug_field', answer); 
		}
	});
}

